# Отримати (радіус) та (центр) круга який можна вписати в цю фігуру. (Найбільший радіус)
circum_radius, circum_center, circumscribed_circle_radius, circles_in_polygons = get_circle_in_polygons(log)

# Вивести отримані результати
def visualize(circum_radius, circum_center, circumscribed_circle_radius, polygons, circles_in_polygons):
    all_points = []
    for polygon in polygons:
        all_points.extend(polygon_to_points(polygon))

    x_vals, y_vals = zip(*all_points)


    fig, ax = plt.subplots(figsize=(10, 10))

    # ax.plot(x_vals, y_vals, marker='o', linestyle='-', color='r', label='Data Points')
    for radius, center in circles_in_polygons:
        circumscribed_circle = plt.Circle(center, radius, color=(random.random(), random.random(), random.random()), fill=False, linewidth=2, label='Circle')
        ax.add_artist(circumscribed_circle)

    circumscribed_circle = plt.Circle(circum_center, circum_radius, color='blue', fill=False, linewidth=2, label='Circum Circle')
    ax.add_artist(circumscribed_circle)

    circumscribed_circle = plt.Circle(circum_center, circumscribed_circle_radius, color='green', fill=False, linewidth=2, label='Circumscribed Circle')
    ax.add_artist(circumscribed_circle)

    all_radii = [r for r, _ in circles_in_polygons]
    max_extent = max(all_radii + [circumscribed_circle_radius]) * 1.5  # Add some padding for visibility


    ax.set_xlim(circum_center[0] - max_extent, circum_center[0] + max_extent)
    ax.set_ylim(circum_center[1] - max_extent, circum_center[1] + max_extent)


    plt.grid(True)
    plt.xlabel('X Coordinate')
    plt.ylabel('Y Coordinate')
    plt.title('Visualization of Points and Circumscribed Circle')

    plt.gca().set_aspect('equal', adjustable='box')
    plt.legend()
    plt.show()


visualize(circum_radius, circum_center, circumscribed_circle_radius, log, circles_in_polygons)